import gql from "graphql-tag";

export const ALL_NEWS_QUERY = gql `query { 
        entries 
        { 
          link 
          description 
          user 
          votes 
          created 
          id 
        } 
      }`

 export const SEARCH_NEWS_QUERY = gql ("query { \
        search ( \
          pattern: \""+searchPattern +"\" \
        ) \
        { \
          link \
          description \
          user \
          votes \
          created \
          id \
        } \
      }")
